      let header = document.querySelector('.portfolio-header');
      const textSection = document.querySelector('.text-section');
      window.addEventListener('scroll', function () {
        const sectionRect = textSection.getBoundingClientRect();
        if (sectionRect.top <= window.innerHeight * 0.5 && sectionRect.bottom >= 0) {
          header.classList.add('is-color');
        } else {
          header.classList.remove('is-color');
        }
      });
    },
