export function useGoMenu() {
  const goMenu = (section) => {
    const targetSection = document.querySelector(`#${section}-section`);
    if (targetSection) {
      targetSection.scrollIntoView({ behavior: "smooth" });
    }
  };
  return { goMenu };
}
