      const tl = gsap.timeline({
        paused: true
      });
      const isMobile = window.innerWidth <= 600;
      const isTablet = window.innerWidth <= 900;
      ScrollTrigger.create({
        trigger: ".visual-section",
        start: isMobile ? "top bottom" : "top 80%",
        onEnter: () => {
          gsap.to(".visual-section", {
            opacity: 1,
            duration: 0
          });
          document.querySelector(".visual-section .visual-icon-grp").classList.add("is-active");
          tl.restart(true, false);
        },
        onEnterBack: () => {
          gsap.to(".visual-section", {
            opacity: 1,
            duration: 0
          });
          document.querySelector(".visual-section .visual-icon-grp").classList.add("is-active");
          tl.restart(true, false);
        },
        onLeave: () => {
          gsap.to(".visual-section", {
            opacity: 0,
            duration: 0.4
          });
          document.querySelector(".visual-section .visual-icon-grp").classList.remove("is-active");
        }
      });
      tl.set(".visual-section", {
        opacity: 1
      });
      const txtDelay = isTablet ? 0 : 0.7;
      tl.to(".visual-section .txt", {
        y: 0,
        opacity: 1,
        duration: 0.3,
        stagger: 0.03,
        ease: "power2.out"
      }, txtDelay);
      const lineDelay = isMobile ? 0.5 : 0.2;
      tl.fromTo(".visual-line, .visual-arrow", {
        x: '-100%'
      }, {
        x: 0,
        duration: isMobile ? 0.3 : 0.5,
        ease: "linear"
      }, lineDelay);
      const iconDelay = isMobile ? 0.5 : 0.2;
      tl.fromTo(".visual-icon", {
        x: '-100%'
      }, {
        x: 0,
        duration: 0.5,
        ease: "linear"
      }, iconDelay);
      const videoDelay = isMobile ? 0.5 : 0.2;
      tl.fromTo(".visual-video-inner", {
        x: '-100%'
      }, {
        x: 0,
        duration: isMobile ? 0.3 : 0.5,
        ease: "linear"
      }, videoDelay);
      tl.to({}, {
        duration: isMobile ? 0.6 : 1.2
      });

      // 아이콘 회전 애니메이션
