    aboutTxtAni() {
      const textElements = gsap.utils.toArray('.about-section .txt');
      textElements.forEach(txt => {
        const isMobile = window.innerWidth <= 600;
        gsap.to(txt, {
          backgroundSize: '100%',
          ease: 'none',
          scrollTrigger: {
            trigger: txt,
            start: isMobile ? 'top 80%' : 'center 100%',
            end: 'center 50%',
            scrub: true
          }
        });
      });
      ScrollTrigger.refresh();
    },
