    worksItemAni() {
      const isMobile = window.innerWidth <= 600;
      const yValue = isMobile ? -40 : -100;
      gsap.utils.toArray(".works-link").forEach((item, idx) => {
        gsap.to(item, {
          yPercent: yValue,
          ease: "none",
          scrollTrigger: {
            trigger: ".works-section",
            start: "top 30%",
            end: "bottom 30%",
            scrub: true
          },
          delay: idx * 1
        });
      });
    },
    // horizon-section 애니메이션
    horizonAni() {
      let tl = gsap.timeline({
        scrollTrigger: {
          trigger: ".horizon-section",
          start: "center center",
          end: "+=3000",
          scrub: 1,
          pin: true
        }
      });

      // 이미지 확대
      const isMobile = window.innerWidth <= 600;
      tl.to(".horizon-img", {
        width: isMobile ? "100%" : window.innerWidth - 70,
        height: isMobile ? "100%" : window.innerHeight - 60,
        duration: isMobile ? 0.7 : 0.3,
        ease: "none"
      });

      // 이미지 왼쪽으로 이동
      tl.to(".horizon-txt-grp", {
        xPercent: -100,
        duration: isMobile ? 1.5 : 3,
        ease: "none"
      }, 1);

      // horizon-section 텍스트 등장 애니메이션
      const lines = gsap.utils.toArray(".horizon-section .txt");
      lines.forEach((line, idx) => {
        const isMobile = window.innerWidth <= 600;
        tl.fromTo(line, {
          opacity: 0,
          yPercent: 100
        }, {
          opacity: 1,
          yPercent: 0,
          duration: isMobile ? 1 : 3,
          delay: 3,
          ease: "power2.out"
        }, 2 + idx * 0.5);
      });
    },
    // 스크롤 시 배경색 바꾸기
    bgChagne() {
      gsap.to("body", {
        backgroundColor: "#fff",
        scrollTrigger: {
          trigger: ".works-section",
          start: "bottom 50%",
          end: "bottom",
          scrub: true
        }
      });
    },
    // text-section .text-title 애니메이션
    textAnimation() {
      gsap.utils.toArray(".text-title").forEach(title => {
        gsap.fromTo(title, {
          autoAlpha: 0,
          opacity: 0
        }, {
          autoAlpha: 1,
          opacity: 1,
          ease: "power1.out",
          scrollTrigger: {
            trigger: title,
            start: "top 70%",
            end: "top 30%",
            scrub: 1
          }
        });
      });

      // .line 요소에 is-active 클래스 추가
      ScrollTrigger.create({
        trigger: ".text-section .line",
        start: "top 80%",
        onEnter: () => {
          document.querySelector(".text-section .line").classList.add("is-active");
