        gsap.to(txt, {
          backgroundSize: '100%',
          ease: 'none',
          scrollTrigger: {
            trigger: txt,
            start: isMobile ? 'top 50%' : 'bottom 100%',
            end: isMobile ? 'bottom 100%' : 'center 50%',
            scrub: true
          }
        });
      });
      ScrollTrigger.refresh();
    },
