import 'gsap/ScrollToPlugin';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { gsap } from 'gsap';
import { Autoplay, FreeMode } from 'swiper/modules';
import OrgItem from './components/OrgItem.vue';
gsap.registerPlugin(ScrollTrigger);
export default {
  name: "Index",
  components: {
    OrgItem
  },
  data() {
    return {
      modules: [Autoplay, FreeMode],
      titles: ['Passionate', 'Smart', 'Flexible'],
      activeIndex: 0,
      exitingIndex: null,
      intervalId: null,
      careerItems: [{
        title: '포켓컴퍼니',
        detail: '웹 퍼블리셔',
        period: '2023.09 - 2024.10'
      }, {
        title: '(주)씨케이앤비',
        detail: '웹 디자이너 / 대리, 파트장',
        period: '2019.03 - 2021.01'
      }],
      eduItems: [{
        title: '리액트 & Node.js',
        period: '2023.04 - 2023.06'
      }, {
        title: 'jQuery & Javascript',
        period: '2022.04 - 2022.06'
      }, {
        title: 'jQuery & Javascript',
        period: '2020.10 - 2020.12'
      }, {
        title: 'HTML & CSS',
        period: '2020.05 - 2020.08'
      }, {
        title: '웹표준 & 접근성',
        period: '2020.02 - 2020.03'
      }],
      licItems: [{
        title: '컴퓨터 그래픽스 운용기능사',
        period: '2018.09'
      }],
      worksItem: [{
        company: '브리에이션',
        desc: '핸드폰 견적, 상담, 기기 수령 등의 서비스를 지원하는 중개 플랫폼의 UI 퍼블리싱을 담당했습니다. 메인 페이지, 회원가입, 로그인, 이용약관, 개인정보처리방침 페이지 등의 퍼블리싱 진행함.',
        skills: ['퍼블리싱 70%', 'VUE', 'CSS', 'JAVASCRIPT', 'GSAP'],
        link: '#'
      }, {
        company: '포켓컴퍼니',
        desc: '사내 서비스 및 브랜드 사이트 다수 퍼블리싱. 프론트 작업과 협업 경험.',
        skills: ['퍼블리싱 80%', 'HTML', 'SCSS', 'JAVASCRIPT', 'VUE'],
        link: '#'
      }]
    };
  },
  mounted() {
    this.visualTxtAni();
    this.worksTitleAni();
    this.worksItemAni();
    this.horizonAni();
    this.bgChagne();
    this.textAnimation();
    this.introAni();
    this.goTopBtn();
    this.cursorDot();
    this.scrollAnimation();
    this.rollingTitleAni();
  },
  beforeUnmount() {
    clearInterval(this.intervalId);
  },
  methods: {
    // 가로 영역에서 애니메이션
    horizonAni() {
      let tl = gsap.timeline({
        scrollTrigger: {
          trigger: ".horizon-section",
          start: "center center",
          end: "+=1500",
          scrub: 1,
          pin: true
        }
      });

      // 이미지 확대
      tl.to(".horizon-img", {
        width: window.innerWidth - 70,
        height: window.innerHeight - 60,
        duration: 0.3,
        ease: "none"
      });

      // 텍스트 이동
      tl.to(".horizon-txt-grp", {
        xPercent: -100,
        duration: 0.7,
        ease: "none"
      }, 1);

      // 텍스트 등장 애니메이션
      const lines = gsap.utils.toArray(".horizon-section .line");
      lines.forEach((line, i) => {
        tl.fromTo(line, {
          opacity: 0,
          yPercent: 100
        }, {
          opacity: 1,
          yPercent: 0,
          duration: 0.6,
          ease: "power2.out"
        }, 1.7 + i * 0.2);
      });
    },
    visualTxtAni() {
      const tl = gsap.timeline({
        paused: true
      });
      ScrollTrigger.create({
        trigger: ".visual-section",
        start: "top 80%",
        onEnter: () => {
          gsap.to(".visual-section", {
            opacity: 1,
            duration: 0
          });
          tl.restart(true, false);
        },
        onEnterBack: () => {
          gsap.to(".visual-section", {
            opacity: 1,
            duration: 0
          });
          tl.restart(true, false);
        },
        onLeave: () => {
          gsap.to(".visual-section", {
            opacity: 0,
            duration: 0.4
          });
        }
      });
      tl.set(".visual-section", {
        opacity: 1
      });
      tl.to(".visual-section .txt", {
        y: 0,
        opacity: 1,
        duration: 0.3,
        stagger: 0.03,
        ease: "power2.out"
      }, 0);
      tl.fromTo(".visual-line, .visual-arrow", {
        x: '-100%'
      }, {
        x: 0,
        duration: 0.3,
        ease: "linear"
      }, 0);
      tl.fromTo(".visual-icon", {
        x: '-100%'
      }, {
        x: 0,
        duration: 0.3,
        ease: "linear"
      }, 0);
      tl.fromTo(".visual-video-inner", {
        x: '-100%'
      }, {
        x: 0,
        duration: 0.3,
        ease: "linear"
      }, 0.1);

      // 멈춤
      tl.to({}, {
        duration: 1.2
      });

      // 아이콘 회전 애니메이션
      gsap.to(".visual-section .ani-spin02", {
        xPercent: 0,
        duration: 1.2,
        ease: "power2.out",
        scrollTrigger: {
          trigger: ".visual-section",
          start: "top 80%",
          toggleActions: "play none none reverse"
        }
      });
    },
    worksTitleAni() {
      ScrollTrigger.create({
        trigger: ".works-section",
        start: "top 80%",
        onEnter: () => {
          document.querySelector(".works-section .line").classList.add("is-active");
        },
        onLeaveBack: () => {
          document.querySelector(".works-section .line").classList.remove("is-active");
        }
      });
    },
    // 스크롤 시 works-item 살짝 올라오는 애니메이션
    worksItemAni() {
      gsap.utils.toArray(".works-item").forEach((item, index) => {
        gsap.to(item, {
          yPercent: -100,
          ease: "none",
          scrollTrigger: {
            trigger: ".works-section",
            start: "top 30%",
            end: "bottom 30%",
            scrub: true
          },
          delay: index * 1
        });
      });
    },
    // 스크롤 시 배경색 바꾸기
    bgChagne() {
      gsap.to("body", {
        backgroundColor: "#fff",
        scrollTrigger: {
          trigger: ".works-section",
          start: "bottom 10%",
          end: "bottom",
          scrub: true
        }
      });
    },
    textAnimation() {
      gsap.utils.toArray(".text-title").forEach(title => {
        gsap.fromTo(title, {
          autoAlpha: 0,
          opacity: 0
        }, {
          autoAlpha: 1,
          opacity: 1,
          ease: "power1.out",
          scrollTrigger: {
            trigger: title,
            start: "top 70%",
            end: "top 30%",
            scrub: 1
          }
        });
      });

      // .line 요소에 is-active 클래스 추가
      ScrollTrigger.create({
        trigger: ".text-section .line",
        start: "top 80%",
        onEnter: () => {
          document.querySelector(".text-section .line").classList.add("is-active");
        },
        onLeaveBack: () => {
          document.querySelector(".text-section .line").classList.remove("is-active");
        }
      });
    },
    // 인트로 애니메이션
    introAni() {
      this.$nextTick(() => {
        document.querySelector('.loader').classList.add('is-ready');
      });
    },
    // 맨 위로 올라가는 버튼
    goTopBtn() {
      let btnTop = document.querySelector('.btn-top');
      btnTop.addEventListener('click', function () {
        window.scroll({
          top: 0,
          behavior: "smooth"
        });
      });
    },
    // 스크롤 시 인트로 흐려지는 애니메이션
    scrollAnimation() {
      gsap.fromTo(".intro-section", {
        opacity: 1.15
      }, {
        opacity: 0,
        ease: "power1.in",
        scrollTrigger: {
          trigger: ".intro-section",
          start: "top center",
          end: "110% center",
          scrub: true,
          toggleActions: "play none none play"
        }
      });
    },
    // 커서포인터 애니메이션
    cursorDot() {
      const cursorSmallDot = document.getElementById("cursor-small-dot");
      let smallDotPosition = {
        x: 0,
        y: 0
      };
      const translate3d = (x, y) => `translate3d(${x}px, ${y}px, 0)`;
      const handleMouseMove = e => {
        smallDotPosition.x = e.clientX + 5;
        smallDotPosition.y = e.clientY + 5;
        cursorSmallDot.style.transform = translate3d(smallDotPosition.x, smallDotPosition.y);
      };
      const mouseMoveHandler = e => {
        window.requestAnimationFrame(() => handleMouseMove(e));
      };
      window.addEventListener("mousemove", mouseMoveHandler);
    },
    // 커서가 a태그를 호버하면 클래스 추가하기
    cursorHover() {
      const cursorSmallDot = document.getElementById("cursor-small-dot");
      const links = document.querySelectorAll('a');
      links.forEach(link => {
        link.addEventListener('mouseover', function () {
          alert("1");
          cursorSmallDot.classList.add('is-hover');
        });
        link.addEventListener('mouseout', function () {
          cursorSmallDot.classList.remove('is-hover');
        });
      });
    },
    // 어바웃섹션 타이틀 롤링 애니메이션
    rollingTitleAni() {
      setInterval(() => {
        this.exitingIndex = this.activeIndex;
        this.activeIndex = (this.activeIndex + 1) % this.titles.length;
        setTimeout(() => {
          this.exitingIndex = null;
        }, 800);
      }, 1500);
    }
  }
};
